package com.jy.web;import com.jy.domain.WordToHtml;import com.jy.service.WordToHtmlService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.*;import org.springframework.web.servlet.ModelAndView;import java.util.List;/** * @author john */@Controllerpublic class WordToHtmlController {    @Autowired    private WordToHtmlService service;    /**     * @return     */    @ModelAttribute("contentlist")    @RequestMapping(value = "/list", method = RequestMethod.GET)    public List<WordToHtml> documents() {        return service.listAll();    }    @RequestMapping(value = "/index")    public ModelAndView index() {        ModelAndView view = new ModelAndView();        view.setViewName("index");        return view;    }}