package com.jy.web;import com.jy.domain.Document;import com.jy.service.WordToHtmlService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.*;import org.springframework.web.servlet.ModelAndView;import javax.servlet.http.HttpServletRequest;import java.util.List;/** * @author john *///@Controller@RestControllerpublic class WordToHtmlController {    @Autowired    private WordToHtmlService service;    /**     * @return     */    @ModelAttribute("doclist")    @RequestMapping(value = "/list", method = RequestMethod.GET)    public List<Document> documents() {        return service.listAll();    }    @RequestMapping("/list")    @ResponseBody    public String index() {        return "hello";    }}